// RightHalf.js

import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import axios from 'axios';

function RightHalf() {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    // Fetch match data from the backend
    const fetchMatches = async () => {
      try {
        const response = await axios.get('http://localhost:3000/matches'); // Update with your backend endpoint
        setMatches(response.data);
      } catch (error) {
        console.error('Error fetching matches:', error);
      }
    };

    fetchMatches();
  }, []);

  return (
    <Box
      sx={{
        backgroundColor: '#cce6ff', // Set your desired background color here for right side
        width: '50%', // Take up half of the screen width
        border: '1px solid black', // Outline for visibility
        padding: '20px', // Padding for spacing
        display: 'flex',
        flexDirection: 'column', // Arrange children vertically
      }}
    >
      <Typography variant="h6" gutterBottom>
        Game Links
      </Typography>
      <Box
        sx={{
          backgroundColor: '#e0e0e0', // Background color for the scrollable box
          border: '1px solid black', // Outline for visibility
          padding: '10px', // Padding for the scrollable box
          flexGrow: 1, // Allow this box to grow and take available space
          overflowY: 'auto', // Enable vertical scrolling
          maxHeight: '80vh', // Limit height for the scroll box
        }}
      >
        {/* Scrollable Content */}
        {matches.map((match, index) => (
          <Box
            key={match.match_id} // Use match_id as the key
            sx={{
              backgroundColor: '#fff', // Background color for each link box
              border: '1px dashed #333', // Example border for section visibility
              marginBottom: '10px', // Spacing between boxes
              padding: '10px', // Padding for the link box
            }}
          >
            <Typography variant="body1">
              <a href={`#match-${match.match_id}`}>
                Match ID: {match.match_id}, Player ID: {match.player_id}, Hero ID: {match.hero_id}
              </a>
            </Typography>
          </Box>
        ))}
      </Box>
    </Box>
  );
}

export default RightHalf;
