import React from 'react';
import { Box, Typography } from '@mui/material';

function RightHalf() {
  const links = [
    'Game Link 1',
    'Game Link 2',
    'Game Link 3',
    'Game Link 4',
    'Game Link 5',
    'Game Link 6',
    'Game Link 7',
    'Game Link 8',
    'Game Link 9',
    'Game Link 10',
    'Game Link 11',
    'Game Link 12',
    'Game Link 13',
    'Game Link 14',
    'Game Link 15',
  ];

  return (
    <Box
      sx={{
        backgroundColor: '#cce6ff', // Set your desired background color here for right side
        width: '50%', // Take up half of the screen width
        border: '1px solid black', // Outline for visibility
        padding: '20px', // Padding for spacing
        display: 'flex',
        flexDirection: 'column', // Arrange children vertically
      }}
    >
      {/* Static content on the right side */}
      <Typography variant="h6" gutterBottom>
        Game Links
      </Typography>
      <Box
        sx={{
          backgroundColor: '#e0e0e0', // Background color for the scrollable box
          border: '1px solid black', // Outline for visibility
          padding: '10px', // Padding for the scrollable box
          flexGrow: 1, // Allow this box to grow and take available space
          overflowY: 'auto', // Enable vertical scrolling
          maxHeight: '80vh', // Limit height for the scroll box
        }}
      >
        {/* Scrollable Content */}
        {links.map((link, index) => (
          <Box
            key={index}
            sx={{
              backgroundColor: '#fff', // Background color for each link box
              border: '1px dashed #333', // Example border for section visibility
              marginBottom: '10px', // Spacing between boxes
              padding: '10px', // Padding for the link box
            }}
          >
            <Typography variant="body1">
              <a href={`#${link.toLowerCase().replace(' ', '')}`}>{link}</a>
            </Typography>
          </Box>
        ))}
      </Box>
    </Box>
  );
}

export default RightHalf;
