import React, { useState, useEffect, useCallback } from 'react';
import { Box, Typography, IconButton, Select, MenuItem } from '@mui/material';
import { NavigateBefore, NavigateNext, Pause, PlayArrow } from '@mui/icons-material'; // Import icons for arrows and pause/play
import '../HeroStyles.css'; // Import hero image CSS styles

function TitleBox() {
  const [heroNames, setHeroNames] = useState({}); // State for hero names
  const [heroIds, setHeroIds] = useState([]); // State for hero ids
  const [currentHeroIndex, setCurrentHeroIndex] = useState(0); // State for current hero index
  const [isTransitioning, setIsTransitioning] = useState(false); // State for transition effect
  const [isPaused, setIsPaused] = useState(false); // State for pause/play

  useEffect(() => {
    const extractedHeroNames = extractHeroNames(); // Extract hero names from CSS classes
    const ids = Object.keys(extractedHeroNames); // Array of hero ids/names

    setHeroNames(extractedHeroNames);
    setHeroIds(ids);
    setCurrentHeroIndex(Math.floor(Math.random() * ids.length)); // Initialize with a random hero index
  }, []);

  const handleClick = () => {
    // Handle clicking on the hero image
    console.log(`Clicked on hero ${heroNames[heroIds[currentHeroIndex]]}`);
    // Replace with your navigation logic or link handling
  };

  function extractHeroNames() {
    // Parse hero names from CSS
    const styleSheets = document.styleSheets;
    let heroNames = {};

    for (let i = 0; i < styleSheets.length; i++) {
      const cssRules = styleSheets[i].cssRules || styleSheets[i].rules;

      if (cssRules) {
        for (let j = 0; j < cssRules.length; j++) {
          const rule = cssRules[j];
          if (rule.selectorText && rule.selectorText.startsWith('.hero-image.hero-')) {
            const className = rule.selectorText.replace('.hero-image.hero-', '');
            const heroName = className.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
            heroNames[className] = heroName;
          }
        }
      }
    }

    // Sort hero names alphabetically
    const sortedHeroNames = Object.keys(heroNames).sort((a, b) => {
      const nameA = heroNames[a].toUpperCase(); // ignore upper and lowercase
      const nameB = heroNames[b].toUpperCase(); // ignore upper and lowercase
      if (nameA < nameB) {
        return -1;
      }
      if (nameA > nameB) {
        return 1;
      }
      return 0;
    }).reduce((sortedObj, key) => {
      sortedObj[key] = heroNames[key];
      return sortedObj;
    }, {});

    return sortedHeroNames;
  }

  const handleLeftArrowClick = useCallback(() => {
    setCurrentHeroIndex(prevIndex => (prevIndex === 0 ? heroIds.length - 1 : prevIndex - 1));
    setIsTransitioning(false); // Disable transition effect for manual navigation
  }, [heroIds]);

  const handleRightArrowClick = useCallback(() => {
    setCurrentHeroIndex(prevIndex => (prevIndex === heroIds.length - 1 ? 0 : prevIndex + 1));
    setIsTransitioning(false); // Disable transition effect for manual navigation
  }, [heroIds]);

  useEffect(() => {
    if (!isPaused) {
      const interval = setInterval(() => {
        setIsTransitioning(true); // Enable transition effect for automatic change
        setTimeout(() => {
          setCurrentHeroIndex(prevIndex => (prevIndex === heroIds.length - 1 ? 0 : prevIndex + 1));
          setIsTransitioning(false);
        }, 500); // Duration of the transition effect
      }, 5300); // Change hero every 10 seconds

      return () => clearInterval(interval);
    }
  }, [isPaused, heroIds]);

  const togglePause = () => {
    setIsPaused(prev => !prev);
    setIsTransitioning(false); // Disable transition effect when paused
  };

  const handleHeroChange = (event) => {
    const selectedHeroId = event.target.value;
    const newIndex = heroIds.findIndex(id => id === selectedHeroId);
    if (newIndex !== -1) {
      setCurrentHeroIndex(newIndex);
      setIsTransitioning(false); // Disable transition effect for manual hero change
    }
  };

  return (
    <Box
      sx={{
        width: 'auto', // Take up remaining width
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px', // Add padding for spacing
        border: '1px solid black', // Outline for visibility (can be removed if not needed)
      }}
    >
      {/* Title */}
      <Typography variant="h4" gutterBottom style={{ marginTop: '-20px' }}>
        Dota Game Tracker
      </Typography>

      {/* Spacer */}
      <Box sx={{ height: '100px' }} />

      {/* Hero Image and Name */}
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          opacity: isTransitioning ? 0 : 1,
          transition: isTransitioning ? 'opacity 0.5s ease-in-out' : 'none',
        }}
      >
        <a
          href={`https://www.dotabuff.com/heroes/${heroNames[heroIds[currentHeroIndex]].toLowerCase().replace(/\s+/g, '-')}`}
          target="_blank"
          rel="noopener noreferrer"
          onClick={handleClick}
        >
          <Box
            className={`hero-image hero-${heroIds[currentHeroIndex]}`} // Dynamically assign hero class
            style={{
              width: '32px',
              height: '32px',
              cursor: 'pointer',
              marginBottom: '20px', // Increased margin between image and name
              transform: 'scale(1.9)', // Magnify by 90%
            }} // Adjust size, cursor style, and magnification
          ></Box>
        </a>

        {/* Hero Name Dropdown */}
        <Select
          value={heroIds[currentHeroIndex]}
          onChange={handleHeroChange}
          sx={{ marginTop: '10px' }}
        >
          {heroIds.map(id => (
            <MenuItem key={id} value={id}>{heroNames[id]}</MenuItem>
          ))}
        </Select>
      </Box>

      {/* Navigation Arrows */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          marginTop: '10px',
        }}
      >
        <IconButton onClick={handleLeftArrowClick}>
          <NavigateBefore />
        </IconButton>
        <IconButton onClick={togglePause}>
          {isPaused ? <PlayArrow /> : <Pause />}
        </IconButton>
        <IconButton onClick={handleRightArrowClick}>
          <NavigateNext />
        </IconButton>
      </Box>
    </Box>
  );
}

export default TitleBox;
