import React, { useState, useEffect } from 'react';
import { Box, Typography, IconButton } from '@mui/material';
import { NavigateBefore, NavigateNext } from '@mui/icons-material'; // Import icons for arrows
import '../HeroStyles.css'; // Import hero image CSS styles

function TitleBox() {
  const heroNames = extractHeroNames(); // Extract hero names from CSS classes
  const heroIds = Object.keys(heroNames); // Array of hero ids/names

  // Initialize state with a random hero index
  const [currentHeroIndex, setCurrentHeroIndex] = useState(() => Math.floor(Math.random() * heroIds.length));

  const heroId = heroIds[currentHeroIndex]; // Current hero id/name based on index

  const handleClick = () => {
    // Handle clicking on the hero image
    console.log(`Clicked on hero ${heroNames[heroId]}`);
    // Replace with your navigation logic or link handling
  };

  function extractHeroNames() {
    // Parse hero names from CSS
    const styleSheets = document.styleSheets;
    let heroNames = {};

    for (let i = 0; i < styleSheets.length; i++) {
      const cssRules = styleSheets[i].cssRules || styleSheets[i].rules;

      if (cssRules) {
        for (let j = 0; j < cssRules.length; j++) {
          const rule = cssRules[j];
          if (rule.selectorText && rule.selectorText.startsWith('.hero-image.hero-')) {
            const className = rule.selectorText.replace('.hero-image.hero-', '');
            const heroName = className.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
            heroNames[className] = heroName;
          }
        }
      }
    }

    return heroNames;
  }

  const handleLeftArrowClick = () => {
    setCurrentHeroIndex(prevIndex => (prevIndex === 0 ? heroIds.length - 1 : prevIndex - 1));
  };

  const handleRightArrowClick = () => {
    setCurrentHeroIndex(prevIndex => (prevIndex === heroIds.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <Box
      sx={{
        width: 'auto', // Take up remaining width
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px', // Add padding for spacing
        border: '1px solid black', // Outline for visibility (can be removed if not needed)
      }}
    >
      {/* Title */}
      <Typography variant="h4" gutterBottom style={{ marginTop: '-20px' }}>
        Dota Game Tracker
      </Typography>

      {/* Spacer */}
      <Box sx={{ height: '100px' }} />

      {/* Hero Image and Name */}
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <a
          href={`https://www.dotabuff.com/heroes/${heroNames[heroId].toLowerCase().replace(/\s+/g, '-')}`}
          target="_blank"
          rel="noopener noreferrer"
          onClick={handleClick}
        >
          <Box
            className={`hero-image hero-${heroId}`} // Dynamically assign hero class
            style={{
              width: '32px',
              height: '32px',
              cursor: 'pointer',
              marginBottom: '20px', // Increased margin between image and name
              transform: 'scale(1.9)', // Magnify by 90%
            }} // Adjust size, cursor style, and magnification
          ></Box>
        </a>

        {/* Hero Name */}
        <Typography variant="body1" gutterBottom>
          {heroNames[heroId]}
        </Typography>
      </Box>

      {/* Navigation Arrows */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          marginTop: '10px',
        }}
      >
        <IconButton onClick={handleLeftArrowClick}>
          <NavigateBefore />
        </IconButton>
        <IconButton onClick={handleRightArrowClick}>
          <NavigateNext />
        </IconButton>
      </Box>
    </Box>
  );
}

export default TitleBox;
