import React, { useState, useEffect, useCallback } from 'react';
import { Box, Typography, IconButton, Select, MenuItem } from '@mui/material';
import { NavigateBefore, NavigateNext, Pause, PlayArrow } from '@mui/icons-material'; // Import icons for arrows and pause/play
import '../HeroStyles.css'; // Import hero image CSS styles

function TitleBox() {
  const [heroData, setHeroData] = useState({
    heroNames: {},
    heroIds: [],
    currentHeroIndex: 0,
    isTransitioning: false,
    isPaused: false
  });

  useEffect(() => {
    const extractedHeroNames = extractHeroNames(); // Extract hero names from CSS classes
    const ids = Object.keys(extractedHeroNames); // Array of hero ids/names

    setHeroData({
      heroNames: extractedHeroNames,
      heroIds: ids,
      currentHeroIndex: Math.floor(Math.random() * ids.length), // Initialize with a random hero index
      isTransitioning: false,
      isPaused: false
    });
  }, []);

  const handleClick = () => {
    console.log(`Clicked on hero ${heroData.heroNames[heroData.heroIds[heroData.currentHeroIndex]]}`);
  };

  function extractHeroNames() {
    const styleSheets = document.styleSheets;
    let heroNames = {};

    for (let i = 0; i < styleSheets.length; i++) {
      const cssRules = styleSheets[i].cssRules || styleSheets[i].rules;

      if (cssRules) {
        for (let j = 0; j < cssRules.length; j++) {
          const rule = cssRules[j];
          if (rule.selectorText && rule.selectorText.startsWith('.hero-image.hero-')) {
            const className = rule.selectorText.replace('.hero-image.hero-', '');
            const heroName = className.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
            heroNames[className] = heroName;
          }
        }
      }
    }

    // Sort hero names alphabetically
    const sortedHeroNames = Object.keys(heroNames).sort((a, b) => {
      const nameA = heroNames[a].toUpperCase(); // ignore upper and lowercase
      const nameB = heroNames[b].toUpperCase(); // ignore upper and lowercase
      if (nameA < nameB) {
        return -1;
      }
      if (nameA > nameB) {
        return 1;
      }
      return 0;
    }).reduce((sortedObj, key) => {
      sortedObj[key] = heroNames[key];
      return sortedObj;
    }, {});

    return sortedHeroNames;
  }

  const handleLeftArrowClick = useCallback(() => {
    setHeroData(prev => ({
      ...prev,
      currentHeroIndex: prev.currentHeroIndex === 0 ? prev.heroIds.length - 1 : prev.currentHeroIndex - 1,
      isTransitioning: false // Disable transition effect for manual navigation
    }));
  }, [heroData.heroIds.length]);

  const handleRightArrowClick = useCallback(() => {
    setHeroData(prev => ({
      ...prev,
      currentHeroIndex: prev.currentHeroIndex === prev.heroIds.length - 1 ? 0 : prev.currentHeroIndex + 1,
      isTransitioning: false // Disable transition effect for manual navigation
    }));
  }, [heroData.heroIds.length]);

  useEffect(() => {
    if (!heroData.isPaused) {
      const interval = setInterval(() => {
        setHeroData(prev => ({
          ...prev,
          isTransitioning: true // Enable transition effect for automatic change
        }));
        setTimeout(() => {
          setHeroData(prev => ({
            ...prev,
            currentHeroIndex: prev.currentHeroIndex === prev.heroIds.length - 1 ? 0 : prev.currentHeroIndex + 1,
            isTransitioning: false
          }));
        }, 500); // Duration of the transition effect
      }, 5300); // Change hero every 10 seconds

      return () => clearInterval(interval);
    }
  }, [heroData.isPaused, heroData.heroIds.length]);

  const togglePause = () => {
    setHeroData(prev => ({
      ...prev,
      isPaused: !prev.isPaused,
      isTransitioning: false // Disable transition effect when paused
    }));
  };

  const handleHeroChange = (event) => {
    const selectedHeroId = event.target.value;
    const newIndex = heroData.heroIds.findIndex(id => id === selectedHeroId);
    if (newIndex !== -1) {
      setHeroData(prev => ({
        ...prev,
        currentHeroIndex: newIndex,
        isTransitioning: false // Disable transition effect for manual hero change
      }));
    }
  };

  return (
    <Box
      sx={{
        width: 'auto', // Take up remaining width
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px', // Add padding for spacing
        border: '1px solid black', // Outline for visibility (can be removed if not needed)
      }}
    >
      {/* Title */}
      <Typography variant="h4" gutterBottom style={{ marginTop: '-20px' }}>
        Dota Game Tracker
      </Typography>

      {/* Spacer */}
      <Box sx={{ height: '100px' }} />

      {/* Hero Image and Name */}
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          opacity: heroData.isTransitioning ? 0 : 1,
          transition: heroData.isTransitioning ? 'opacity 0.5s ease-in-out' : 'none',
        }}
      >
        <a
          href={`https://www.dotabuff.com/heroes/${heroData.heroNames[heroData.heroIds[heroData.currentHeroIndex]].toLowerCase().replace(/\s+/g, '-')}`}
          target="_blank"
          rel="noopener noreferrer"
          onClick={handleClick}
        >
          <Box
            className={`hero-image hero-${heroData.heroIds[heroData.currentHeroIndex]}`} // Dynamically assign hero class
            style={{
              width: '32px',
              height: '32px',
              cursor: 'pointer',
              marginBottom: '20px', // Increased margin between image and name
              transform: 'scale(1.9)', // Magnify by 90%
            }} // Adjust size, cursor style, and magnification
          ></Box>
        </a>

        {/* Hero Name Dropdown */}
        <Select
          value={heroData.heroIds[heroData.currentHeroIndex]}
          onChange={handleHeroChange}
          sx={{ marginTop: '10px' }}
        >
          {heroData.heroIds.map(id => (
            <MenuItem key={id} value={id}>{heroData.heroNames[id]}</MenuItem>
          ))}
        </Select>
      </Box>

      {/* Navigation Arrows */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          marginTop: '10px',
        }}
      >
        <IconButton onClick={handleLeftArrowClick}>
          <NavigateBefore />
        </IconButton>
        <IconButton onClick={togglePause}>
          {heroData.isPaused ? <PlayArrow /> : <Pause />}
        </IconButton>
        <IconButton onClick={handleRightArrowClick}>
          <NavigateNext />
        </IconButton>
      </Box>
    </Box>
  );
}

export default TitleBox;
