import React from 'react';
import { Box, Typography, IconButton } from '@mui/material';
import { NavigateBefore, NavigateNext } from '@mui/icons-material'; // Import icons for arrows
import { heroImagesMapping, heroNamesMapping } from './heroMapping'; // Import hero images and names mapping

function TitleBox({ currentHeroIndex, onLeftArrowClick, onRightArrowClick }) {
  const heroIds = Object.keys(heroImagesMapping); // Array of hero ids from mapping object
  const heroId = heroIds[currentHeroIndex]; // Current hero id based on index

  const handleClick = () => {
    // Handle clicking on the hero image
    console.log(`Clicked on hero ${heroNamesMapping[heroId]}`);
    // Replace with your navigation logic or link handling
  };

  return (
    <Box
      sx={{
        width: 'auto', // Take up remaining width
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px', // Add padding for spacing
        border: '1px solid black', // Outline for visibility (can be removed if not needed)
      }}
    >
      {/* Title */}
      <Typography variant="h4" gutterBottom>
        Dota Game Tracker
      </Typography>

      {/* Hero Image */}
      <Box
        className={`hero-image ${heroImagesMapping[heroId]}`} // Dynamically assign hero class
        style={{ width: '80px', height: '80px', cursor: 'pointer', marginTop: '10px' }} // Adjust size and cursor style
        onClick={handleClick} // Handle click on the hero image
      ></Box>

      {/* Hero Name */}
      <Typography variant="body1" gutterBottom>
        {heroNamesMapping[heroId]}
      </Typography>

      {/* Navigation Arrows */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          marginTop: '10px',
        }}
      >
        <IconButton
          onClick={onLeftArrowClick}
          disabled={currentHeroIndex === 0} // Disable if at the first hero
        >
          <NavigateBefore />
        </IconButton>
        <IconButton
          onClick={onRightArrowClick}
          disabled={currentHeroIndex === heroIds.length - 1} // Disable if at the last hero
        >
          <NavigateNext />
        </IconButton>
      </Box>
    </Box>
  );
}

export default TitleBox;
